pageFunction.prefill:
// see https://apify.com/apify/web-scraper#page-function 
async function pageFunction(context) {
    const $ = context.jQuery;
    const pageTitle = $('title').first().text();
    let data = { url: context.request.url }

    if(context.request.userData && context.request.userData.type === 'from_pagination'){        
        data.img = $(' [data-qa-id=adview_spotlight_container] img').attr('src')
        data.title = $('[data-qa-id=adview_title]').text()
        data.price = $('[data-qa-id=adview_price]:eq(0)').text()
        data.date = $('[data-qa-id=adview_date]').text()
        data.description = $('[data-qa-id=adview_description_container]').text()
        data.link = context.request.userData.link
        data.type = 'product'
    }else{
        let items = []
        $('[data-qa-id=aditem_container]').map( function(i){
            let obj = {}
            obj.link = "https://www.leboncoin.fr" + $(this).attr('href')
            obj.img = $(this).find('img').attr('src')
            obj.title = $(this).find('[data-qa-id=aditem_title]').text()
            obj.price = $(this).find('[data-qa-id=aditem_price]').text()
            obj.type = 'from_pagination'
            items.push( JSON.parse(JSON.stringify(obj)) )
        })

        items.forEach(async item => {
            await context.enqueueRequest({ url: item.link, userData: item }, { forefront: true });
        })
        // uncomment this to see while browsing what items you see in the search
        // data.items = items
    }

    context.log.info(`URL: ${context.request.url}, TITLE: ${pageTitle}, Nbr: ${context.request.userData}`);
    return data;
}
